import React, { useState } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";

const AddProduct = () => {
  const [images, setImages] = useState([]);
  const [productDetails, setProductDetails] = useState({
    id_categoria: 1, // Asigna el id de la categoría correspondiente
    nombre_producto: "",
    descripcion_producto: "",
    imagen_producto1: "",
    imagen_producto2: "",
    imagen_producto3: ""
  });

  const AddProduct = async () => {
    const formData = new FormData();
    images.forEach((image, index) => {
      formData.append(`imagen_producto${index + 1}`, image);
    });

    await fetch("http://localhost:4000/upload", {
      method: "POST",
      body: formData
    })
      .then((resp) => resp.json())
      .then((data) => {
        if (data.success) {
          const { image_urls } = data;
          const productData = { ...productDetails };
          image_urls.forEach((url, index) => {
            productData[`imagen_producto${index + 1}`] = url;
          });

          fetch("http://localhost:4000/addproduct", {
            method: "POST",
            headers: {
              Accept: "application/json",
              "Content-Type": "application/json"
            },
            body: JSON.stringify(productData)
          })
            .then((resp) => resp.json())
            .then((data) => {
              if (data.success) {
                alert("Producto agregado");
              } else {
                alert("Error al agregar el producto");
              }
            });
        } else {
          alert("Error al subir imágenes");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const changeHandler = (e) => {
    setProductDetails({ ...productDetails, [e.target.name]: e.target.value });
  };

  const imageHandler = (e) => {
    const files = Array.from(e.target.files);
    setImages(files);
  };

  return (
    <div className="addproduct">
      <div className="addproduct-itemfield">
        <p>Nombre del producto</p>
        <input
          type="text"
          name="nombre_producto"
          value={productDetails.nombre_producto}
          onChange={(e) => {
            changeHandler(e);
          }}
          placeholder="Escribe aquí"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Descripción del producto</p>
        <input
          type="text"
          name="descripcion_producto"
          value={productDetails.descripcion_producto}
          onChange={(e) => {
            changeHandler(e);
          }}
          placeholder="Escribe aquí"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Seleccionar imágenes</p>
        <label htmlFor="file-input">
          <img
            className="addproduct-thumbnail-img"
            src={!images.length ? upload_area : URL.createObjectURL(images[0])}
            alt=""
          />
        </label>
        <input
          onChange={(e) => {
            imageHandler(e);
          }}
          type="file"
          name="image"
          id="file-input"
          multiple
          hidden
        />
      </div>
      <button className="addproduct-btn" onClick={() => AddProduct()}>
        AGREGAR
      </button>
    </div>
  );
};

export default AddProduct;
