import React, { useEffect, useState } from "react";
import "./ListProduct.css";
import cross_icon from '../Assets/cross_icon.png';
import editboton from '../Assets/edit-boton.png';

const ListProduct = () => {
  const [allProducts, setAllProducts] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [itemProducts, setItemProducts] = useState([]);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:4000/allproducts');
      const data = await response.json();
      setAllProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const fetchItemProducts = async (productId) => {
    try {
      const response = await fetch(`http://localhost:4000/itemproducts?id_producto=${productId}`);
      const data = await response.json();
      setItemProducts(data);
    } catch (error) {
      console.error('Error fetching item products:', error);
    }
  };

  const updateItemProducts = async (itemProducts) => {
    try {
      await fetch('http://localhost:4000/updateitemproducts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(itemProducts),
      });
      // Actualizar la lista de productos después de modificar los item_products
      fetchProducts();
      // Salir del modo de edición
      setEditMode(false);
      // Limpiar los itemProducts
      setItemProducts([]);
    } catch (error) {
      console.error('Error updating item products:', error);
    }
  };  
  

  useEffect(() => {
    fetchProducts();
  }, []);

  const removeProduct = async (id) => {
    try {
      await fetch('http://localhost:4000/removeproduct', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id: id }),
      });
      // Actualizar la lista de productos después de eliminar
      fetchProducts();
    } catch (error) {
      console.error('Error removing product:', error);
    }
  };

  const handleEdit = async (product) => {
    setSelectedProduct(product);
    await fetchItemProducts(product.id); // Obtener los detalles del item_product
    setEditMode(true);
  };

  useEffect(() => {
    // Código para actualizar itemProducts aquí
  }, [itemProducts]); // Ejecutar cuando itemProducts cambie
  
  const handleCantidadChange = (e, itemId) => {
    const { value } = e.target;
    setItemProducts(prevItemProducts => {
      const updatedItemProducts = prevItemProducts.map(item => {
        if (item.id === itemId) {
          return { ...item, cantidad_disp: value };
        }
        return item;
      });
      return updatedItemProducts;
    });
  };
  
  const handlePrecioChange = (e, itemId) => {
    const { value } = e.target;
    setItemProducts(prevItemProducts => {
      const updatedItemProducts = prevItemProducts.map(item => {
        if (item.id === itemId) {
          return { ...item, precio: value };
        }
        return item;
      });
      return updatedItemProducts;
    });
  };
  
  const handleEstadoChange = (e, itemId) => {
    const { value } = e.target;
    setItemProducts(prevItemProducts => {
      const updatedItemProducts = prevItemProducts.map(item => {
        if (item.id === itemId) {
          return { ...item, estado: value };
        }
        return item;
      });
      return updatedItemProducts;
    });
  };

  return (
    <div className="listproduct">
      <h1>All Products List</h1>
      <div className="listproduct-format-main">
        <p>IMAGEN</p>
        <p>NOMBRE</p>
        <p>DESCRIPCION</p>
        <p>CATEGORIA</p>
        <p>ELIMINAR</p>
        <p>EDITAR</p>
      </div>
      <div className="listproduct-allproducts">
        <hr />
        {allProducts.map((product) => (
          <div key={product.id}>
            <div className="listproduct-format-main listproduct-format">
              <img className="listproduct-product-icon" src={product.imagen_producto1} alt="" />
              <p>{product.nombre_producto}</p>
              <p>{product.descripcion_producto}</p>
              <p>{product.nombre_categoria}</p>
              {/* Agregar el botón de eliminar con la función removeProduct */}
              <img className="listproduct-remove-icon" onClick={() => removeProduct(product.id)} src={cross_icon} alt="" />
              <img className="listproduct-edit-icon" onClick={() => handleEdit(product)} src={editboton} alt="" />
            </div>
            <hr />
          </div>
        ))}
      </div>
      {editMode && (
        <div className="edit-product-menu">
          <h2>Edit Product: {selectedProduct.nombre_producto}</h2>
          <div>
            {/* Mostrar los detalles del item_product */}
            {itemProducts.map((item) => (
              <div key={item.id}>
                <p>Cantidad: <input type="number" value={item.cantidad_disp} onChange={(e) => {
  const updatedItemProducts = itemProducts.map(i => {
    if (i.id === item.id) {
      return { ...i, cantidad_disp: e.target.value };
    }
    return i;
  });
  setItemProducts(updatedItemProducts);
}} /></p>
<p>Precio: <input type="number" value={item.precio} onChange={(e) => {
  const updatedItemProducts = itemProducts.map(i => {
    if (i.id === item.id) {
      return { ...i, precio: e.target.value };
    }
    return i;
  });
  setItemProducts(updatedItemProducts);
}} /></p>
<p>Estado: <input type="text" value={item.estado} onChange={(e) => {
  const updatedItemProducts = itemProducts.map(i => {
    if (i.id === item.id) {
      return { ...i, estado: e.target.value };
    }
    return i;
  });
  setItemProducts(updatedItemProducts);
}} /></p>

              </div>
            ))}
          </div>
          <button onClick={() => updateItemProducts(itemProducts)}>Guardar</button>
          <button onClick={() => setEditMode(false)}>Cancelar</button>
        </div>
      )}
    </div>
  );
};

export default ListProduct;
