import React, { useEffect, useState } from "react";
import "./ListProduct.css";
import cross_icon from '../Assets/cross_icon.png';
import editboton from '../Assets/edit-boton.png';

const ListProduct = () => {
  const [allProducts, setAllProducts] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [itemProducts, setItemProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [itemName, setItemName] = useState('');
  const [itemDescription, setItemDescription] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [categoryOptions, setCategoryOptions] = useState([]);
  const [image1, setImage1] = useState(null);
  const [image2, setImage2] = useState(null);
  const [image3, setImage3] = useState(null);


  const fetchProducts = async () => {
    try {
      let url = 'http://localhost:4000/allproducts';
      // Si hay un término de búsqueda, agregarlo a la URL
      if (searchTerm.trim() !== '') {
        url = `http://localhost:4000/searchproduct?search=${encodeURIComponent(searchTerm)}`;
      }
      const response = await fetch(url);
      const data = await response.json();
      setAllProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [searchTerm]); // Ejecutar fetchProducts cuando cambie searchTerm

  const updateItemProducts = async () => {
    try {
      await fetch('http://localhost:4000/updateitemproducts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(itemProducts),
      });
      // Actualizar la lista de productos después de modificar los item_products
      fetchProducts();
      // Salir del modo de edición
      setEditMode(false);
      // Limpiar los itemProducts
      setItemProducts([]);
    } catch (error) {
      console.error('Error updating item products:', error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:4000/allcategories');
      const data = await response.json();
      setCategoryOptions(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const removeProduct = async (id) => {
    try {
      await fetch('http://localhost:4000/removeproduct', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id: id }),
      });
      // Actualizar la lista de productos después de eliminar
      fetchProducts();
    } catch (error) {
      console.error('Error removing product:', error);
    }
  };

  const handleEdit = (product) => {
    setSelectedProduct(product);
    setEditMode(true);
    setItemName(product.nombre_producto);
    setItemDescription(product.descripcion_producto);
    setCategoryId(product.id_categoria);
  };

  const handleImageChange = (event, imageIndex) => {
    const file = event.target.files[0];
    switch (imageIndex) {
      case 1:
        setImage1(file);
        break;
      case 2:
        setImage2(file);
        break;
      case 3:
        setImage3(file);
        break;
      default:
        break;
    }
  
    // Actualizar la imagen en la interfaz de usuario
    const productId = selectedProduct.id; // Asegúrate de tener el producto seleccionado correctamente
    const imageElement = document.getElementById(`productImage-${productId}-${imageIndex}`);
    if (imageElement) {
      const reader = new FileReader();
      reader.onload = (e) => {
        imageElement.src = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  };
  

  const handleSave = async () => {
    try {
      const formData = new FormData();
      formData.append('nombre_producto', itemName);
      formData.append('descripcion_producto', itemDescription);
      formData.append('id_categoria', categoryId);
      formData.append('imagen_producto1', image1);
      formData.append('imagen_producto2', image2);
      formData.append('imagen_producto3', image3);

      await fetch(`http://localhost:4000/updateproduct/${selectedProduct.id}`, {
        method: 'POST',
        body: formData,
      });

      setEditMode(false);
      setSelectedProduct(null);
      fetchProducts();
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  const handleCancel = () => {
    setEditMode(false);
    setSelectedProduct(null);
  };

  return (
    <div className="listproduct">
      <h1>All Products List</h1>
      <input
        type="text"
        placeholder="Buscar producto..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <div className="listproduct-format-main">
        <p>IMAGEN</p>
        <p>NOMBRE</p>
        <p>DESCRIPCION</p>
        <p>CATEGORIA</p>
        <p>ELIMINAR</p>
        <p>EDITAR</p>
      </div>
      <div className="listproduct-allproducts">
        <hr />
        {allProducts.map((product) => (
          <div key={product.id}>
            <div className="listproduct-format-main listproduct-format">
              <img className="listproduct-product-icon" src={product.imagen_producto1} alt="" />
              <p>{product.nombre_producto}</p>
              <p>{product.descripcion_producto}</p>
              <p>{product.nombre_categoria}</p>
              <img className="listproduct-remove-icon" onClick={() => removeProduct(product.id)} src={cross_icon} alt="" />
              <img className="listproduct-edit-icon" onClick={() => handleEdit(product)} src={editboton} alt="" />
            </div>
            <hr />
          </div>
        ))}
      </div>
      {editMode && (
        <div className="edit-product-menu">
          <h2>Edit Product: {selectedProduct.nombre_producto}</h2>
          <input type="text" value={itemName} onChange={(e) => setItemName(e.target.value)} />
          <input type="text" value={itemDescription} onChange={(e) => setItemDescription(e.target.value)} />
          <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)}>
            <option value="">Select category</option>
            {categoryOptions.map(category => (
              <option key={category.id} value={category.id}>{category.nombre_categoria}</option>
            ))}
          </select>
          <img
  className="listproduct-product-icon"
  src={product.imagen_producto1}
  alt=""
  onClick={() => document.getElementById(`fileInput-${product.id}-1`).click()}
/>
<input
  id={`fileInput-${product.id}-1`}
  type="file"
  accept="image/*"
  style={{ display: 'none' }}
  onChange={(e) => handleImageChange(e, 1)}
/>
<img
  className="listproduct-product-icon"
  src={product.imagen_producto2}
  alt=""
  onClick={() => document.getElementById(`fileInput-${product.id}-1`).click()}
/>
<input
  id={`fileInput-${product.id}-1`}
  type="file"
  accept="image/*"
  style={{ display: 'none' }}
  onChange={(e) => handleImageChange(e, 1)}
/>
<img
  className="listproduct-product-icon"
  src={product.imagen_producto3}
  alt=""
  onClick={() => document.getElementById(`fileInput-${product.id}-1`).click()}
/>
<input
  id={`fileInput-${product.id}-1`}
  type="file"
  accept="image/*"
  style={{ display: 'none' }}
  onChange={(e) => handleImageChange(e, 1)}
/>
          <button onClick={handleSave}>Guardar</button>
          <button onClick={handleCancel}>Cancelar</button>
        </div>
      )}
    </div>
  );
};

export default ListProduct;
