import React, { useState, useEffect } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";

const AddProduct = () => {
  const [images, setImages] = useState([null, null, null]);
  const [productDetails, setProductDetails] = useState({
    nombre_producto: "",
    descripcion_producto: "",
    precio: "",
    cantidad: "",
    id_categoria: "",
    id_variacion: "",
    id_opcion_variacion: "",
    estado: "1",
  });
  const [categorias, setCategorias] = useState([]);
  const [newCategoryName, setNewCategoryName] = useState("");
  const [parentCategories, setParentCategories] = useState([]);

  useEffect(() => {
    fetchCategorias();
    fetchParentCategories();
  }, []);

  const fetchCategorias = async () => {
    try {
      const response = await fetch('http://localhost:4000/categories');
      const data = await response.json();
      setCategorias(data);
    } catch (error) {
      console.error('Error fetching categorias:', error);
    }
  };

  const fetchParentCategories = async () => {
    try {
      const response = await fetch('http://localhost:4000/parentcategories');
      const data = await response.json();
      setParentCategories(data);
    } catch (error) {
      console.error('Error fetching parent categories:', error);
    }
  };

  const handleGuardar = async () => {
    try {
      // Lógica para enviar la nueva categoría al backend
      const nuevaCategoria = {
        nombre_categoria: newCategoryName,
        id_categoria_padre: productDetails.id_categoria // Aquí deberías obtener el ID de la categoría padre seleccionada
      };
      const response = await fetch('http://localhost:4000/addcategory', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(nuevaCategoria),
      });
      // Aquí deberías manejar la respuesta del backend
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Error al agregar la categoría:', error);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProductDetails({ ...productDetails, [name]: value });
  };

  const handleImageChange = (event, index) => {
    const newImages = [...images];
    newImages[index] = event.target.files[0];
    setImages(newImages);
  };

  const handleCategoriaChange = (event) => {
    setProductDetails({ ...productDetails, id_categoria: event.target.value });
  };

  const handleNewCategoryChange = (event) => {
    setNewCategoryName(event.target.value);
  };

  return (
    <div className="addproduct">
      <div className="addproduct-itemfield">
        <p>Nombre del producto</p>
        <input
          type="text"
          name="nombre_producto"
          value={productDetails.nombre_producto}
          onChange={handleInputChange}
          placeholder="Ingrese aquí"
        />
      </div>
      {/* Otros campos omitidos para brevedad */}
      <div className="addproduct-itemfield">
        <p>Categoría</p>
        <select
          value={productDetails.id_categoria}
          name="id_categoria"
          className="add-product-selector"
          onChange={handleCategoriaChange}
        >
          <option value="" disabled>
            Seleccione una categoría
          </option>
          {categorias.map(categoria => (
            <option key={categoria.id} value={categoria.id}>
              {categoria.nombre_categoria}
            </option>
          ))}
        </select>
      </div>
      <div className="addproduct-itemfield">
        <p>Nueva Categoría</p>
        <input
          type="text"
          value={newCategoryName}
          onChange={handleNewCategoryChange}
          placeholder="Ingrese el nombre de la nueva categoría"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Categoría Padre (Opcional)</p>
        <select
          value={productDetails.id_categoria_padre}
          name="id_categoria_padre"
          className="add-product-selector"
          onChange={handleCategoriaPadreChange}
        >
          <option value="" disabled>
            Seleccione una categoría padre
          </option>
          {parentCategories.map(categoria => (
            <option key={categoria.id} value={categoria.id}>
              {categoria.nombre_categoria}
            </option>
          ))}
        </select>
      </div>
      <button className="addproduct-btn" onClick={handleGuardar}>
        AÑADIR CATEGORÍA
      </button>
    </div>
  );
};

export default AddProduct;
