import React, { useEffect, useState } from "react";
import "./ListProduct.css";
import cross_icon from '../Assets/cross_icon.png';
import editboton from '../Assets/edit-boton.png';

const ListProduct = () => {
  const [allProducts, setAllProducts] = useState([]);
  const [editingProductId, setEditingProductId] = useState(null);
  const [editProducts, setEditProducts] = useState([]);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:4000/allproducts');
      const data = await response.json();
      setAllProducts(data);
      console.log(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const fetchEditProducts = async (productId) => {
    try {
      const response = await fetch(`http://localhost:4000/editproducts/${productId}`);
      const data = await response.json();
      setEditProducts(data);
      console.log(data);
    } catch (error) {
      console.error('Error fetching edit products:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const removeProduct = async (id) => {
    try {
      await fetch('http://localhost:4000/removeproduct', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id: id }),
      });
      // Actualizar la lista de productos despuÃ©s de eliminar
      fetchProducts();
    } catch (error) {
      console.error('Error removing product:', error);
    }
  };

  const handleEditClick = (productId) => {
    setEditingProductId(productId);
    fetchEditProducts(productId);
  };

  return (
    <div className="listproduct">
      <h1>All Products List</h1>
      <div className="listproduct-format-main">
        <p>IMAGEN</p>
        <p>NOMBRE</p>
        <p>DESCRIPCION</p>
        <p>CATEGORIA</p>
        <p>ELIMINAR</p>
        <p>EDITAR</p>
      </div>
      <div className="listproduct-allproducts">
        <hr />
        {allProducts.map((product) => (
          <div key={product.id}>
            <div className="listproduct-format-main listproduct-format">
              <img className="listproduct-product-icon" src={product.imagen_producto1} alt="" />
              <p>{product.nombre_producto}</p>
              <p>{product.descripcion_producto}</p>
              <p>{product.nombre_categoria}</p>
              {/* Agregar el botÃ³n de eliminar con la funciÃ³n removeProduct */}
              <img className="listproduct-remove-icon" onClick={() => removeProduct(product.id)} src={cross_icon} alt="" />
              <img className="listproduct-edit-icon" onClick={() => handleEditClick(product.id)} src={editboton} alt="" />
            </div>
            <hr />
          </div>
        ))}
      </div>
      {editingProductId && (
        <div className="edit-product-details">
          <h2>Edit Product Details</h2>
          <div className="edit-product-details-list">
            {editProducts.map((item) => (
              <div key={item.id}>
                <p>Cantidad Disponible: {item.cantidad_disp}</p>
                <p>Precio: {item.precio}</p>
                <p>Estado: {item.estado}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ListProduct;
