import React, { useState, useEffect } from "react";
import "./AddProduct.css";
import cross_icon from "../Assets/cross_icon.png";

const AddCategory = () => {
  const [categoriaDetails, setCategoriaDetails] = useState({
    nombre_categoria: "",
    id_categoria_padre: ""
  });
  const [categoriasPadre, setCategoriasPadre] = useState([]);

  useEffect(() => {
    fetchCategoriasPadre();
  }, []);

  const fetchCategoriasPadre = async () => {
    try {
      const response = await fetch('http://localhost:4000/categories/null');
      const data = await response.json();
      setCategoriasPadre(data);
    } catch (error) {
      console.error('Error fetching categorias padre:', error);
    }
  };

  const handleGuardarCategoria = async () => {
    try {
      const response = await fetch('http://localhost:4000/addcategory', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(categoriaDetails),
      });
      const data = await response.json();

      if (data.success) {
        alert('Categoría agregada correctamente');
      } else {
        alert('Error al agregar la categoría');
      }
    } catch (error) {
      console.error('Error al agregar la categoría:', error);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setCategoriaDetails({ ...categoriaDetails, [name]: value });
  };

  return (
    <div className="addcategory">
      <div className="addcategory-itemfield">
        <p>Nombre de la categoría</p>
        <input
          type="text"
          name="nombre_categoria"
          value={categoriaDetails.nombre_categoria}
          onChange={handleInputChange}
          placeholder="Ingrese aquí"
        />
      </div>
      <div className="addcategory-itemfield">
        <p>Categoría Padre</p>
        <select
          value={categoriaDetails.id_categoria_padre}
          name="id_categoria_padre"
          className="add-category-selector"
          onChange={handleInputChange}
        >
          <option value="" disabled>
            Seleccione una categoría padre
          </option>
          {categoriasPadre.map(categoria => (
            <option key={categoria.id} value={categoria.id}>
              {categoria.nombre_categoria}
            </option>
          ))}
        </select>
      </div>
      <button className="addcategory-btn" onClick={handleGuardarCategoria}>
        AÑADIR CATEGORÍA
      </button>
    </div>
  );
};

export default AddCategory;
