import React, { useState } from "react";
import "./AddPromotions.css";

const AddPromotions = () => {
  const [promotionDetails, setPromotionDetails] = useState({
    nombre: "",
    descripcion: "",
    descuento_porcentaje: "",
    fecha_inicio: "",
    fecha_final: ""
  });
  const [categorias, setCategorias] = useState([]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setPromotionDetails({ ...promotionDetails, [name]: value });
  };

  const handleGuardar = async () => {
    try {
      const response = await fetch('http://localhost:4000/addpromotions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(promotionDetails),
      });
      const data = await response.json();
      if (data.success) {
        alert('Promoción agregada exitosamente');
      } else {
        alert('Error al agregar la promoción');
      }
    } catch (error) {
      console.error('Error al agregar la promoción:', error);
    }
  };

  return (
    <div className="add-promotion">
      <div className="add-promotion-itemfield">
        <p>Nombre de la promoción</p>
        <input
          type="text"
          name="nombre"
          value={promotionDetails.nombre}
          onChange={handleInputChange}
          placeholder="Ingrese aquí"
        />
      </div>
      <div className="add-promotion-itemfield">
        <p>Descripción</p>
        <input
          type="text"
          name="descripcion"
          value={promotionDetails.descripcion}
          onChange={handleInputChange}
          placeholder="Ingrese aquí"
        />
      </div>
      <div className="add-promotion-itemfield">
        <p>Descuento (%)</p>
        <input
          type="number"
          name="descuento_porcentaje"
          value={promotionDetails.descuento_porcentaje}
          onChange={handleInputChange}
          placeholder="Ingrese aquí"
        />
      </div>
      <div className="add-promotion-itemfield">
        <p>Fecha de inicio</p>
        <input
          type="date"
          name="fecha_inicio"
          value={promotionDetails.fecha_inicio}
          onChange={handleInputChange}
        />
      </div>
      <div className="add-promotion-itemfield">
        <p>Fecha final</p>
        <input
          type="date"
          name="fecha_final"
          value={promotionDetails.fecha_final}
          onChange={handleInputChange}
        />
      </div>
      <button className="add-promotion-btn" onClick={handleGuardar}>
        AÑADIR
      </button>
    </div>
  );
};

export default AddPromotions;
