import React, { useState, useEffect } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";

const AddProduct = () => {
  const [image, setImage] = useState(null);
  const [productDetails, setProductDetails] = useState({
    name: "",
    description: "",
    category: "",
    variation: "",
    option: "",
    old_price: "",
    quantity: "",
    images: {
      image1: null,
      image2: null,
      image3: null
    }
  });
  const [categories, setCategories] = useState([]);
  const [variations, setVariations] = useState([]);
  const [options, setOptions] = useState([]);

  useEffect(() => {
    fetchCategories();
    fetchVariations();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await fetch("http://localhost:4000/categories");
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const fetchVariations = async () => {
    try {
      const response = await fetch("http://localhost:4000/variations");
      const data = await response.json();
      setVariations(data);
    } catch (error) {
      console.error("Error fetching variations:", error);
    }
  };

  const fetchOptions = async (variationId) => {
    try {
      const response = await fetch(`http://localhost:4000/options/${variationId}`);
      const data = await response.json();
      setOptions(data);
    } catch (error) {
      console.error("Error fetching options:", error);
    }
  };

  const handleCategoryChange = (e) => {
    setProductDetails({ ...productDetails, category: e.target.value });
  };

  const handleVariationChange = (e) => {
    const variationId = e.target.value;
    setProductDetails({ ...productDetails, variation: variationId });
    fetchOptions(variationId);
  };

  const handleOptionChange = (e) => {
    setProductDetails({ ...productDetails, option: e.target.value });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProductDetails({ ...productDetails, [name]: value });
  };

  const handleImageChange = (e, imageNumber) => {
    const file = e.target.files[0];
    setImage({ ...image, [imageNumber]: file });
  };

  const addProduct = async () => {
    try {
      const response = await fetch("http://localhost:4000/addproduct", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(productDetails)
      });
      if (response.ok) {
        // Product added successfully, perform any additional actions here
        console.log("Product added successfully");
      } else {
        console.error("Failed to add product:", response.statusText);
      }
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  return (
    <div className="addproduct">
      <div className="addproduct-itemfield">
        <p>Product title</p>
        <input
          type="text"
          name="name"
          value={productDetails.name}
          onChange={handleInputChange}
          placeholder="Type here"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Description</p>
        <input
          type="text"
          name="description"
          value={productDetails.description}
          onChange={handleInputChange}
          placeholder="Type here"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Category</p>
        <select
          value={productDetails.category}
          name="category"
          onChange={handleCategoryChange}
        >
          <option value="">Select category</option>
          {categories.length > 0 && categories.map((category) => (
  <option key={category.id} value={category.id}>
    {category.nombre}
  </option>
))}

        </select>
      </div>
      <div className="addproduct-itemfield">
        <p>Variation</p>
        <select
          value={productDetails.variation}
          name="variation"
          onChange={handleVariationChange}
        >
          <option value="">Select variation</option>
          {variations.map((variation) => (
            <option key={variation.id} value={variation.id}>
              {variation.nombre}
            </option>
          ))}
        </select>
      </div>
      <div className="addproduct-itemfield">
        <p>Option</p>
        <select
          value={productDetails.option}
          name="option"
          onChange={handleOptionChange}
        >
          <option value="">Select option</option>
          {options.map((option) => (
            <option key={option.id} value={option.id}>
              {option.valor}
            </option>
          ))}
        </select>
      </div>
      <div className="addproduct-price">
        <div className="addproduct-itemfield">
          <p>Price</p>
          <input
            type="text"
            name="old_price"
            value={productDetails.old_price}
            onChange={handleInputChange}
            placeholder="Type here"
          />
        </div>
        <div className="addproduct-itemfield">
          <p>Quantity</p>
          <input
            type="text"
            name="quantity"
            value={productDetails.quantity}
            onChange={handleInputChange}
            placeholder="Type here"
          />
        </div>
      </div>
      <div className="addproduct-itemfield">
        <p>Product Images</p>
        <input
          type="file"
          onChange={(e) => handleImageChange(e, "image1")}
          accept="image/*"
        />
        <input
          type="file"
          onChange={(e) => handleImageChange(e, "image2")}
          accept="image/*"
        />
        <input
          type="file"
          onChange={(e) => handleImageChange(e, "image3")}
          accept="image/*"
        />
      </div>
      <button className="addproduct-btn" onClick={addProduct}>
        ADD
      </button>
    </div>
  );
};

export default AddProduct;
