import React, { useState, useEffect } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";

function AddProduct() {
  const [categorias, setCategorias] = useState([]);
  const [variaciones, setVariaciones] = useState([]);
  const [opcionesVariacion, setOpcionesVariacion] = useState([]);

  useEffect(() => {

    fetchCategorias();
    fetchVariaciones();
    fetchOpcionesVariacion();
  }, []);

  const fetchCategorias = async () => {
    try {
      const response = await fetch('http://localhost:4000/categories');
      const data = await response.json();
      setCategorias(data);
    } catch (error) {
      console.error('Error fetching categorias:', error);
    }
  };

  const fetchVariaciones = async () => {
    // Lógica similar para obtener variaciones
  };

  const fetchOpcionesVariacion = async () => {
    // Lógica similar para obtener opciones de variación
  };

  return (
    <div className="add-product-container">
      <h1>Agregar Producto</h1>
      <div className="form-container">
        <input type="text" placeholder="Nombre" />
        <input type="text" placeholder="Descripción" />
        <input type="number" placeholder="Precio" />
        <input type="number" placeholder="Cantidad" />
        <select>
          {categorias.map(categoria => (
            <option key={categoria.id} value={categoria.id}>
              {categoria.nombre_categoria}
            </option>
          ))}
        </select>
        <select>
          {variaciones.map(variacion => (
            <option key={variacion.id} value={variacion.id}>
              {variacion.nombre}
            </option>
          ))}
        </select>
        <select>
          {opcionesVariacion.map(opcion => (
            <option key={opcion.id} value={opcion.id}>
              {opcion.valor}
            </option>
          ))}
        </select>
        <button>Guardar</button>
      </div>
    </div>
  );
}

export default AddProduct;
