import React, { useState } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";

const AddProduct = () => {
  const [image, setImage] = useState(null);
  const [productDetails, setProductDetails] = useState({
    nombre_producto: "",
    descripcion_producto: "",
    precio: "",
    cantidad: "",
    id_categoria: "",
  });

  const handleGuardar = async () => {
    try {
      const formData = new FormData();
      formData.append("imagen_producto", image);

      const response = await fetch("http://localhost:4000/upload", {
        method: "POST",
        headers: {
          Accept: "application/json",
        },
        body: formData,
      });
      const dataObj = await response.json();

      if (dataObj.success) {
        const product = {
          ...productDetails,
          imagen_producto: dataObj.image_url,
        };

        const addProductResponse = await fetch(
          "http://localhost:4000/addproduct",
          {
            method: "POST",
            headers: {
              Accept: "application/json",
              "Content-Type": "application/json",
            },
            body: JSON.stringify(product),
          }
        );

        const addProductData = await addProductResponse.json();

        if (addProductData.success) {
          alert("Product Added");
        } else {
          alert("Failed to add product");
        }
      }
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProductDetails({ ...productDetails, [name]: value });
  };

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  return (
    <div className="addproduct">
      <div className="addproduct-itemfield">
        <p>Nombre del producto</p>
        <input
          type="text"
          name="nombre_producto"
          value={productDetails.nombre_producto}
          onChange={handleInputChange}
          placeholder="Ingrese aquí"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Descripción</p>
        <input
          type="text"
          name="descripcion_producto"
          value={productDetails.descripcion_producto}
          onChange={handleInputChange}
          placeholder="Ingrese aquí"
        />
      </div>
      <div className="addproduct-price">
        <div className="addproduct-itemfield">
          <p>Precio</p>
          <input
            type="number"
            name="precio"
            value={productDetails.precio}
            onChange={handleInputChange}
            placeholder="Ingrese aquí"
          />
        </div>
        <div className="addproduct-itemfield">
          <p>Cantidad</p>
          <input
            type="number"
            name="cantidad"
            value={productDetails.cantidad}
            onChange={handleInputChange}
            placeholder="Ingrese aquí"
          />
        </div>
      </div>
      <div className="addproduct-itemfield">
        <p>Categoría</p>
        <select
          value={productDetails.id_categoria}
          name="id_categoria"
          className="add-product-selector"
          onChange={handleInputChange}
        >
          <option value="" disabled>
            Seleccione una categoría
          </option>
          {/* Aquí mapea las categorías disponibles */}
        </select>
      </div>
      <div className="addproduct-itemfield">
        <p>Imagen del producto</p>
        <label htmlFor="file-input">
          <img
            className="addproduct-thumbnail-img"
            src={!image ? upload_area : URL.createObjectURL(image)}
            alt=""
          />
        </label>
        <input
          onChange={handleImageChange}
          type="file"
          name="imagen_producto"
          id="file-input"
          hidden
        />
      </div>
      <button className="addproduct-btn" onClick={handleGuardar}>
        AÑADIR
      </button>
    </div>
  );
};

export default AddProduct;
