import React, { useEffect, useState } from "react";
import "./ListProduct.css";
import cross_icon from '../Assets/cross_icon.png';

const ListProduct = () => {
  const [allProducts, setAllProducts] = useState([]);

  useEffect(() => {
    fetchAllProducts();
  }, []);

  const fetchAllProducts = async () => {
    try {
      const response = await fetch('http://localhost:4000/allproducts');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setAllProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const removeProduct = async (id) => {
    try {
      await fetch('http://localhost:4000/removeproduct', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id: id }),
      });
      fetchAllProducts(); // Actualizar la lista de productos después de eliminar uno
    } catch (error) {
      console.error('Error removing product:', error);
    }
  };

  return (
    <div className="listproduct">
      <h1>All Products List</h1>
      {allProducts.map((product) => (
        <div key={product.ID} className="product-item">
          <p>Product ID: {product.ID}</p>
          <p>Category ID: {product.ID_CATEGORIA}</p>
          <p>Product Name: {product.NOMBRE_PRODUCTO}</p>
          <p>Product Description: {product.DESCRIPCION_PRODUCTO}</p>
          <img className="product-image" src={product.IMAGEN_PRODUCTO1} alt="" />
          <img className="remove-icon" onClick={() => removeProduct(product.ID)} src={cross_icon} alt="" />
          <hr />
        </div>
      ))}
    </div>
  );
};

export default ListProduct;
