import React, { useState, useEffect } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";

function AddProduct() {
  const [categorias, setCategorias] = useState([]);
  const [variaciones, setVariaciones] = useState([]);
  const [opcionesVariacion, setOpcionesVariacion] = useState([]);
  const [idVariacionSeleccionada, setIdVariacionSeleccionada] = useState("");
  const [variacionBloqueada, setVariacionBloqueada] = useState(false);

  useEffect(() => {
    fetchCategorias();
    fetchVariaciones();
  }, []);

  const fetchCategorias = async () => {
    try {
      const response = await fetch('http://localhost:4000/categories');
      const data = await response.json();
      setCategorias(data);
    } catch (error) {
      console.error('Error fetching categorias:', error);
    }
  };

  const fetchVariaciones = async () => {
    try {
      const response = await fetch('http://localhost:4000/variations');
      const data = await response.json();
      setVariaciones(data);
    } catch (error) {
      console.error('Error fetching variaciones:', error);
    }
  };

  const fetchOpcionesVariacion = async (idVariacion) => {
    try {
      const response = await fetch(`http://localhost:4000/options?idVariacion=${idVariacion}`);
      const data = await response.json();
      setOpcionesVariacion(data);
    } catch (error) {
      console.error('Error fetching opciones:', error);
    }
  };

  useEffect(() => {
    if (idVariacionSeleccionada) {
      fetchOpcionesVariacion(idVariacionSeleccionada);
    }
  }, [idVariacionSeleccionada]);

  const handleVariacionChange = (event) => {
    const idVariacion = event.target.value;
    setIdVariacionSeleccionada(idVariacion);
    setVariacionBloqueada(true); // Bloquea la variación después de seleccionarla
    setOpcionesVariacion([]); // Limpia las opciones de variación al cambiar la variación
  };

  const handleReiniciarVariaciones = () => {
    setIdVariacionSeleccionada(""); // Reinicia la selección de variaciones
    setVariacionBloqueada(false); // Desbloquea la selección de variaciones
    setOpcionesVariacion([]); // Reinicia las opciones de variación
  };

  return (
    <div className="add-product-container">
      <h1>Agregar Producto</h1>
      <div className="form-container">
        <div className="form-row">
          <input type="text" placeholder="Nombre" />
          <input type="text" placeholder="Descripción" />
        </div>
        <div className="form-row">
          <input type="number" placeholder="Precio" />
          <input type="number" placeholder="Cantidad" />
        </div>
        <select>
          <option disabled selected>Selecciona una categoría</option>
          {categorias.map(categoria => (
            <option key={categoria.id} value={categoria.id}>
              {categoria.nombre_categoria}
            </option>
          ))}
        </select>
        <select onChange={handleVariacionChange} disabled={variacionBloqueada}>
          <option disabled selected>Selecciona una variación</option>
          {variaciones.map(variacion => (
            <option key={variacion.id} value={variacion.id}>
              {variacion.nombre}
            </option>
          ))}
        </select>
        <select>
          <option disabled selected>Selecciona una opción de variación</option>
          {opcionesVariacion.map(opcion => (
            <option key={opcion.id} value={opcion.id}>
              {opcion.valor}
            </option>
          ))}
        </select>
        <button onClick={handleReiniciarVariaciones}>Reiniciar Variaciones</button>
      </div>
    </div>
  );
}

export default AddProduct;
