import React, { useState, useEffect } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";

const AddProduct = () => {
  const [image, setImage] = useState(null);
  const [productDetails, setProductDetails] = useState({
    name: "",
    description: "",
    category: "",
    variations: [],
    new_price: "",
    old_price: "",
    quantity: "",
    images: {
      image1: null,
      image2: null,
      image3: null
    }
  });
  const [categories, setCategories] = useState([]);
  const [variations, setVariations] = useState([]);
  const [selectedVariation, setSelectedVariation] = useState(null);
  const [variationOptions, setVariationOptions] = useState([]);

  useEffect(() => {
    // Fetch categories and variations from the backend
    fetchCategories();
    fetchVariations();
  }, []);

  const fetchCategories = async () => {
    // Fetch categories from the backend API
    try {
      const response = await fetch("http://localhost:4000/categories");
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const fetchVariations = async () => {
    // Fetch variations from the backend API
    try {
      const response = await fetch("http://localhost:4000/variations");
      const data = await response.json();
      setVariations(data);
    } catch (error) {
      console.error("Error fetching variations:", error);
    }
  };

  const handleCategoryChange = (e) => {
    setProductDetails({ ...productDetails, category: e.target.value });
  };

  const handleVariationChange = (e) => {
    const selectedVariationId = parseInt(e.target.value);
    const selectedVariation = variations.find(
      (variation) => variation.id === selectedVariationId
    );
    setSelectedVariation(selectedVariation);
    setVariationOptions([]);
    fetchVariationOptions(selectedVariationId);
  };

  const fetchVariationOptions = async (variationId) => {
    // Fetch variation options based on the selected variation
    try {
      const response = await fetch(
        `http://localhost:4000/variations/${variationId}/options`
      );
      const data = await response.json();
      setVariationOptions(data);
    } catch (error) {
      console.error("Error fetching variation options:", error);
    }
  };

  const handleOptionChange = (e) => {
    const selectedOptionId = parseInt(e.target.value);
    // Handle the selected option
    // Here you can update the state or perform any action based on the selected option
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProductDetails({ ...productDetails, [name]: value });
  };

  const handleImageChange = (e, imageNumber) => {
    const file = e.target.files[0];
    setImage({ ...image, [imageNumber]: file });
  };

  const addProduct = async () => {
    // First, upload images
    const uploadedImageUrls = await uploadImages();

    // Then, create the product object
    const product = {
      name: productDetails.name,
      description: productDetails.description,
      category: productDetails.category,
      variations: productDetails.variations,
      new_price: productDetails.new_price,
      old_price: productDetails.old_price,
      quantity: productDetails.quantity,
      images: uploadedImageUrls
    };

    // Finally, send the product data to the backend
    try {
      const response = await fetch("http://localhost:4000/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(product)
      });
      const data = await response.json();
      if (data.success) {
        alert("Product Added");
      } else {
        alert("Failed to add product");
      }
    } catch (error) {
      console.error("Error adding product:", error);
      alert("Failed to add product");
    }
  };

  const uploadImages = async () => {
    const formData = new FormData();
    Object.values(image).forEach((file, index) => {
      formData.append(`image${index + 1}`, file);
    });

    try {
      const response = await fetch("http://localhost:4000/upload-images", {
        method: "POST",
        body: formData
      });
      const data = await response.json();
      return data.imageUrls;
    } catch (error) {
      console.error("Error uploading images:", error);
      return [];
    }
  };

  return (
    <div className="addproduct">
      <div className="addproduct-itemfield">
        <p>Product title</p>
        <input
          type="text"
          name="name"
          value={productDetails.name}
          onChange={handleInputChange}
          placeholder="Type here"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Description</p>
        <input
          type="text"
          name="description"
          value={productDetails.description}
          onChange={handleInputChange}
          placeholder="Type here"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Category</p>
        <select
          value={productDetails.category}
          name="category"
          onChange={handleCategoryChange}
        >
          <option value="">Select category</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </div>
      {selectedVariation && (
        <div className="addproduct-itemfield">
          <p>Variation</p>
          <select onChange={handleVariationChange}>
            <option value="">Select variation</option>
            {variations.map((variation) => (
              <option key={variation.id} value={variation.id}>
                {variation.name}
              </option>
            ))}
          </select>
        </div>
      )}
      {selectedVariation && (
        <div className="addproduct-itemfield">
          <p>Option</p>
          <select onChange={handleOptionChange}>
            <option value="">Select option</option>
            {variationOptions.map((option) => (
              <option key={option.id} value={option.id}>
                {option.value}
              </option>
            ))}
          </select>
        </div>
      )}
      <div className="addproduct-price">
        <div className="addproduct-itemfield">
          <p>Price</p>
          <input
            type="text"
            name="old_price"
            value={productDetails.old_price}
            onChange={handleInputChange}
            placeholder="Type here"
          />
        </div>
        <div className="addproduct-itemfield">
          <p>Offer Price</p>
          <input
            type="text"
            name="new_price"
            value={productDetails.new_price}
            onChange={handleInputChange}
            placeholder="Type here"
          />
        </div>
        <div className="addproduct-itemfield">
          <p>Quantity</p>
          <input
            type="text"
            name="quantity"
            value={productDetails.quantity}
            onChange={handleInputChange}
            placeholder="Type here"
          />
        </div>
      </div>
      <div className="addproduct-itemfield">
        <p>Product Images</p>
        <input
          type="file"
          onChange={(e) => handleImageChange(e, "image1")}
          accept="image/*"
        />
        <input
          type="file"
          onChange={(e) => handleImageChange(e, "image2")}
          accept="image/*"
        />
        <input
          type="file"
          onChange={(e) => handleImageChange(e, "image3")}
          accept="image/*"
        />
      </div>
      <button className="addproduct-btn" onClick={addProduct}>
        ADD
      </button>
    </div>
  );
};

export default AddProduct;
