import React, { useState, useEffect } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";

function AddProduct() {
  const [categorias, setCategorias] = useState([]);
  const [variaciones, setVariaciones] = useState([]);
  const [opcionesVariacion, setOpcionesVariacion] = useState([]);
  const [idVariacionSeleccionada, setIdVariacionSeleccionada] = useState(null);
  const [nombre, setNombre] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [precio, setPrecio] = useState(0);
  const [cantidad, setCantidad] = useState(0);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState("");
  const [opcionVariacionSeleccionada, setOpcionVariacionSeleccionada] = useState("");
  const [imagen1, setImagen1] = useState(null);
  const [imagen2, setImagen2] = useState(null);
  const [imagen3, setImagen3] = useState(null);

  useEffect(() => {
    fetchCategorias();
    fetchVariaciones();
  }, []);

  const fetchCategorias = async () => {
    try {
      const response = await fetch('http://localhost:4000/categories');
      const data = await response.json();
      setCategorias(data);
    } catch (error) {
      console.error('Error fetching categorias:', error);
    }
  };

  const fetchVariaciones = async () => {
    try {
      const response = await fetch('http://localhost:4000/variations');
      const data = await response.json();
      setVariaciones(data);
    } catch (error) {
      console.error('Error fetching variaciones:', error);
    }
  };

  const fetchOpcionesVariacion = async (idVariacion) => {
    try {
      const response = await fetch(`http://localhost:4000/options?idVariacion=${idVariacion}`);
      const data = await response.json();
      setOpcionesVariacion(data);
    } catch (error) {
      console.error('Error fetching opciones:', error);
    }
  };

  useEffect(() => {
    if (idVariacionSeleccionada) {
      fetchOpcionesVariacion(idVariacionSeleccionada);
    }
  }, [idVariacionSeleccionada]);

  const handleVariacionChange = (event) => {
    const idVariacion = event.target.value;
    setIdVariacionSeleccionada(idVariacion);
  };

  const handleGuardar = async () => {
    try {
      const data = {
        nombre,
        descripcion,
        precio,
        cantidad,
        categoria: categoriaSeleccionada,
        variacion: idVariacionSeleccionada,
        opcionVariacion: opcionVariacionSeleccionada,
        imagen1,
        imagen2,
        imagen3
      };

      const response = await fetch('http://localhost:4000/addproduct', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });
  
      if (response.ok) {
        console.log('Producto guardado exitosamente.');
        // Reiniciar los estados o hacer alguna otra acciÃ³n luego de guardar
      } else {
        console.error('Error al guardar el producto.');
      }
    } catch (error) {
      console.error('Error al guardar el producto:', error);
    }
  };

  const handleImagen1Change = (event) => {
    setImagen1(event.target.files[0]);
  };

  const handleImagen2Change = (event) => {
    setImagen2(event.target.files[0]);
  };

  const handleImagen3Change = (event) => {
    setImagen3(event.target.files[0]);
  };

  return (
    <div className="add-product-container">
      <h1>Agregar Producto</h1>
      <div className="form-container">
        <div className="form-row">
          <input type="text" placeholder="Nombre" onChange={(e) => setNombre(e.target.value)} />
          <input type="text" placeholder="DescripciÃ³n" onChange={(e) => setDescripcion(e.target.value)} />
        </div>
        <div className="form-row">
          <input type="number" placeholder="Precio" onChange={(e) => setPrecio(e.target.value)} />
          <input type="number" placeholder="Cantidad" onChange={(e) => setCantidad(e.target.value)} />
        </div>
        <select onChange={(e) => setCategoriaSeleccionada(e.target.value)}>
          <option disabled selected>Selecciona una categorÃ­a</option>
          {categorias.map(categoria => (
            <option key={categoria.id} value={categoria.id}>
              {categoria.nombre_categoria}
            </option>
          ))}
        </select>
        <select onChange={handleVariacionChange}>
          <option disabled selected>Selecciona una variaciÃ³n</option>
          {variaciones.map(variacion => (
            <option key={variacion.id} value={variacion.id}>
              {variacion.nombre}
            </option>
          ))}
        </select>
        <select onChange={(e) => setOpcionVariacionSeleccionada(e.target.value)}>
          <option disabled selected>Selecciona una opciÃ³n de variaciÃ³n</option>
          {opcionesVariacion.map(opcion => (
            <option key={opcion.id} value={opcion.id}>
              {opcion.valor}
            </option>
          ))}
        </select>
        <div className="form-row">
          <input type="file" onChange={handleImagen1Change} />
          <input type="file" onChange={handleImagen2Change} />
          <input type="file" onChange={handleImagen3Change} />
        </div>
        <button onClick={handleGuardar}>Guardar</button>
      </div>
    </div>
  );
}

export default AddProduct;
